        protected void SeedUsers(DbContext context)
        {
            var userStore = new UserStore<User>(context);
            var userManager = new UserManager<User>(userStore);
            var user = new User { UserName = "luis" };
            userManager.Create(user, "password");
            SeededUser = user;
        }
        protected void SeedFeedNews(DbContext context)
        {
