                    new FeedNews{ Id = 1, Name = "Reddit", Categories = categories },
                    new FeedNews{ Id = 2, Name = "BBC News", Categories = categories },
                    new FeedNews{ Id = 3, Name = "The New York Times", Categories = categories },
                    new FeedNews{ Id = 4, Name = "BuzzFeed", Categories = categories },
                    new FeedNews{ Id = 5, Name = "E-International", Categories = categories },
                    new FeedNews{ Id = 6, Name = "Defence Blog", Categories = categories }
                });
            context.SaveChanges();
        }
